// MARK: - Reusable Section Header struct SectionHeader: View { let title: String let isLeading: Bool var body: some View { HStack { if isLeading { Image("schedule") .resizable() .scaledToFit() .frame(width: 48.w, height: 48.h) } Text(title) .font(.system(size: 28.sp, weight: .semibold)) .foregroundColor(Color(hex: "#68270F")) if !isLeading { Image(systemName: "chevron.right") .font(.system(size: 24.sp)) .foregroundColor(Color(hex: "#68270F")) } Spacer(minLength: 0) } .padding(.horizontal, 16.w) } }